import React, { useContext, useState, useEffect } from 'react';
import { NotificationContext } from '../context/NotificationContext';

function HospitalDashboard() {
  const { notifications } = useContext(NotificationContext);
  const hospitalName = localStorage.getItem('hospital');
  const hospitalId = notifications.find(notification => notification.hospitalName === hospitalName)?.hospitalId;

  const filteredNotifications = notifications.filter(note => note.hospitalId === hospitalId);

  // Dark mode state
  const [darkMode, setDarkMode] = useState(false);

  useEffect(() => {
    const savedMode = localStorage.getItem('darkMode') === 'true';
    setDarkMode(savedMode);
  }, []);

  const toggleDarkMode = () => {
    setDarkMode(prev => {
      localStorage.setItem('darkMode', !prev);
      return !prev;
    });
  };

  return (
    <div className={`container py-5 ${darkMode ? 'bg-dark text-white' : 'bg-light text-dark'}`} style={{ minHeight: '100vh' }}>
      <div className="text-center mb-5">
        <h2 className="fw-bold text-danger">üè• Welcome, {hospitalName}</h2>
        <p className="text-muted">Monitor live accident alerts assigned to your hospital.</p>

        {/* Toggle button for dark mode */}
        <button className="btn btn-outline-secondary mt-3" onClick={toggleDarkMode}>
          {darkMode ? '‚òÄÔ∏è Light Mode' : 'üåô Dark Mode'}
        </button>
      </div>

      <div className={`card shadow-sm ${darkMode ? 'bg-secondary text-white' : ''}`}>
        <div className="card-body">
          <h4 className="card-title text-center mb-4 text-danger">üö® Real-time Accident Notifications</h4>

          {filteredNotifications.length === 0 ? (
            <div className={`alert ${darkMode ? 'alert-dark' : 'alert-info'} text-center`}>
              No notifications yet. Stay alert for incoming emergencies!
            </div>
          ) : (
            <ul className="list-group">
              {filteredNotifications.map((note, index) => (
                <li key={index} className={`list-group-item d-flex justify-content-between align-items-center ${darkMode ? 'bg-dark text-white border-secondary' : ''}`}>
                  <span>
                    üìç Location: <strong>{note.location}</strong>
                  </span>
                  <span className="badge bg-danger rounded-pill">{note.time}</span>
                </li>
              ))}
            </ul>
          )}
        </div>
      </div>
    </div>
  );
}

export default HospitalDashboard;
