import React, { useState, useContext } from "react";
import { hospitalsData } from "../data/hospitalsData";
import { NotificationContext } from "../context/NotificationContext";

const SearchNearby = () => {
  const [searchTerm, setSearchTerm] = useState("");
  const [foundHospitals, setFoundHospitals] = useState([]);
  const { addNotification } = useContext(NotificationContext);
  const [location, setLocation] = useState("");

  const handleSearch = () => {
    const results = hospitalsData.filter(
      (hosp) =>
        hosp.location.toLowerCase().includes(searchTerm.toLowerCase()) ||
        hosp.name.toLowerCase().includes(searchTerm.toLowerCase())
    );
    setFoundHospitals(results);
  };

  const handleNotify = (hospitalId) => {
    if (location.trim() === "") {
      alert("Please enter accident location");
      return;
    }
    addNotification(hospitalId, location);
    alert("Hospital Notified!");
    setLocation("");
  };

  return (
    <div style={{ textAlign: "center", marginTop: "50px" }}>
      <h2>Search Nearby Hospitals</h2>
      <input
        type="text"
        placeholder="Enter City or Pincode"
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
        style={{ padding: "8px", width: "300px", margin: "10px" }}
      />
      <button onClick={handleSearch} style={{ padding: "8px 16px" }}>
        Search
      </button>

      <div style={{ marginTop: "30px" }}>
        {foundHospitals.map((hospital) => (
          <div key={hospital.id} style={{ border: "1px solid #ccc", padding: "15px", margin: "10px", borderRadius: "8px" }}>
            <h4>{hospital.name}</h4>
            <p>Location: {hospital.location}</p>
            <p>Phone: {hospital.phone}</p>
            <input
              type="text"
              placeholder="Accident Location"
              value={location}
              onChange={(e) => setLocation(e.target.value)}
              style={{ padding: "6px", width: "250px" }}
            />
            <br />
            <button onClick={() => handleNotify(hospital.id)} style={{ marginTop: "10px", padding: "6px 12px" }}>
              Notify Hospital
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default SearchNearby;
