import React, { useState, useContext } from 'react';
import { hospitalsData } from '../data/hospitalsData';
import { NotificationContext } from '../context/NotificationContext';

function Home() {
  const [query, setQuery] = useState('');
  const [results, setResults] = useState([]);
  const { setNotifications } = useContext(NotificationContext);

  const searchHospitals = () => {
    const res = hospitalsData.filter(
      h => h.city.toLowerCase() === query.toLowerCase() || h.pincode === query
    );
    setResults(res);
  };

  const notifyHospital = (hospital) => {
    const location = prompt("Enter your accident location:");
    if(location) {
      setNotifications(prev => [...prev, { hospitalId: hospital.id, location, time: new Date().toLocaleTimeString() }]);
      alert(`Hospital ${hospital.name} notified!`);
    }
  };

  return (
    <div className="container mt-4">
      <h2>Find Nearby Hospitals</h2>
      <div className="input-group mb-3">
        <input
          type="text"
          className="form-control"
          placeholder="Enter City or Pincode"
          value={query}
          onChange={(e) => setQuery(e.target.value)}
        />
        <button className="btn btn-primary" onClick={searchHospitals}>Search</button>
      </div>

      <div className="row">
        {results.map(hospital => (
          <div key={hospital.id} className="col-md-4 mb-3">
            <div className="card">
              <div className="card-body">
                <h5 className="card-title">{hospital.name}</h5>
                <p className="card-text">City: {hospital.city}<br />Pincode: {hospital.pincode}</p>
                <button className="btn btn-success" onClick={() => notifyHospital(hospital)}>Notify Hospital</button>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Home;
