// src/components/SearchNearby.js
import React, { useState } from "react";
import { hospitalsData } from "../data/hospitalsData";
import { useNotifications } from "../context/NotificationContext";

const SearchNearby = () => {
  const [searchTerm, setSearchTerm] = useState("");
  const [results, setResults] = useState([]);
  const [selectedHospital, setSelectedHospital] = useState(null);
  const [locationInput, setLocationInput] = useState("");
  const { addNotification } = useNotifications();

  const handleSearch = (e) => {
    e.preventDefault();
    const filtered = hospitalsData.filter((hospital) =>
      hospital.location.toLowerCase().includes(searchTerm.toLowerCase())
    );
    setResults(filtered);
  };

  const handleNotifyClick = (hospital) => {
    setSelectedHospital(hospital);
  };

  const handleSendNotification = () => {
    if (selectedHospital && locationInput.trim() !== "") {
      addNotification(selectedHospital.id, locationInput);
      alert("Notification sent successfully!");
      setSelectedHospital(null);
      setLocationInput("");
    }
  };

  return (
    <div style={{ textAlign: "center", marginTop: "20px" }}>
      <h2>Search Nearby Hospitals</h2>
      <form onSubmit={handleSearch}>
        <input
          type="text"
          placeholder="Enter city or pincode"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          required
          style={{ padding: "8px", width: "300px" }}
        />
        <button type="submit" style={{ marginLeft: "10px", padding: "8px 16px" }}>
          Search
        </button>
      </form>

      <div style={{ marginTop: "20px" }}>
        {results.length > 0 ? (
          results.map((hospital) => (
            <div key={hospital.id} style={{ marginBottom: "10px", border: "1px solid gray", padding: "10px", borderRadius: "8px" }}>
              <h4>{hospital.name}</h4>
              <p>Location: {hospital.location}</p>
              <button onClick={() => handleNotifyClick(hospital)} style={{ padding: "6px 12px" }}>
                Notify Hospital
              </button>
            </div>
          ))
        ) : (
          <p>No results yet.</p>
        )}
      </div>

      {selectedHospital && (
        <div style={{ marginTop: "30px" }}>
          <h3>Send Accident Location to {selectedHospital.name}</h3>
          <input
            type="text"
            placeholder="Enter accident location"
            value={locationInput}
            onChange={(e) => setLocationInput(e.target.value)}
            style={{ padding: "8px", width: "300px" }}
          />
          <button onClick={handleSendNotification} style={{ marginLeft: "10px", padding: "8px 16px" }}>
            Send
          </button>
        </div>
      )}
    </div>
  );
};

export default SearchNearby;
