// src/components/HospitalLogin.js
import React, { useState } from "react";
import { hospitalsData } from "../data/hospitalsData";

const HospitalLogin = ({ onHospitalLogin }) => {
  const [hospitalName, setHospitalName] = useState("");
  const [password, setPassword] = useState("");

  const handleLogin = (e) => {
    e.preventDefault();
    const hospital = hospitalsData.find(h => h.name === hospitalName && h.password === password);
    if (hospital) {
      onHospitalLogin(hospital.id); // Important: Pass hospital.id, not name
    } else {
      alert("Invalid credentials!");
    }
  };

  return (
    <div style={{ textAlign: "center", marginTop: "50px" }}>
      <h2>Hospital Login</h2>
      <form onSubmit={handleLogin}>
        <input
          type="text"
          placeholder="Hospital Name"
          value={hospitalName}
          onChange={(e) => setHospitalName(e.target.value)}
          style={{ padding: "8px", width: "300px", margin: "10px" }}
        />
        <br />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          style={{ padding: "8px", width: "300px", margin: "10px" }}
        />
        <br />
        <button type="submit" style={{ padding: "8px 16px", marginTop: "10px" }}>
          Login
        </button>
      </form>
    </div>
  );
};

export default HospitalLogin;
