import React, { useState, useContext, useEffect } from 'react';
import { hospitalsData } from '../data/hospitalsData';
import { NotificationContext } from '../context/NotificationContext';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import L from 'leaflet';

// Fix Leaflet icon issues
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: 'https://unpkg.com/leaflet@1.9.4/dist/images/marker-icon-2x.png',
  iconUrl: 'https://unpkg.com/leaflet@1.9.4/dist/images/marker-icon.png',
  shadowUrl: 'https://unpkg.com/leaflet@1.9.4/dist/images/marker-shadow.png',
});

function Home() {
  const [userLocation, setUserLocation] = useState(null);
  const [nearestHospital, setNearestHospital] = useState(null);
  const { setNotifications } = useContext(NotificationContext);

  // Haversine formula
  const getDistanceFromLatLonInKm = (lat1, lon1, lat2, lon2) => {
    const R = 6371;
    const dLat = (lat2 - lat1) * Math.PI / 180;
    const dLon = (lon2 - lon1) * Math.PI / 180;
    const a =
      Math.sin(dLat / 2) * Math.sin(dLat / 2) +
      Math.cos(lat1 * Math.PI / 180) *
      Math.cos(lat2 * Math.PI / 180) *
      Math.sin(dLon / 2) *
      Math.sin(dLon / 2);
    const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
    return R * c;
  };

  useEffect(() => {
    navigator.geolocation.getCurrentPosition(
      (position) => {
        setUserLocation({
          latitude: position.coords.latitude,
          longitude: position.coords.longitude
        });
      },
      () => {
        alert('Unable to retrieve your location.');
      }
    );
  }, []);

  const searchNearestHospital = () => {
    if (!userLocation) {
      alert('Location not available yet.');
      return;
    }

    const { latitude, longitude } = userLocation;

    const hospitalsWithDistance = hospitalsData.map(h => ({
      ...h,
      distance: getDistanceFromLatLonInKm(latitude, longitude, h.latitude, h.longitude)
    }));

    hospitalsWithDistance.sort((a, b) => a.distance - b.distance);

    if (hospitalsWithDistance.length === 0 || hospitalsWithDistance[0].distance > 50) {
      setNearestHospital(null);
      alert('‚ùó No hospitals found within 50 km.');
      return;
    }

    setNearestHospital(hospitalsWithDistance[0]);
  };

  const notifyHospital = (hospital) => {
    const location = prompt("Enter your accident location (City Name):");

    if (location && userLocation) {
      setNotifications(prev => [
        ...prev,
        {
          hospitalId: hospital.id,
          hospitalName: hospital.name,
          location,
          latitude: userLocation.latitude,
          longitude: userLocation.longitude,
          time: new Date().toLocaleTimeString()
        }
      ]);
      alert(`üöë Hospital "${hospital.name}" has been notified!`);
    }
  };

  return (
    <div className="container-fluid p-0">
      <div className="bg-dark text-white text-center py-5">
        <div className="container">
          <h1 className="display-4 fw-bold">Accident Detection & Emergency Notifier</h1>
          <p className="lead">Automatically locate and notify the nearest hospital.</p>
        </div>
      </div>

      <div className="container py-5">
        <div className="text-center mb-4">
          <h2 className="text-danger fw-bold">Nearest Hospital</h2>
          <p className="text-muted">Click the button below to find the closest hospital to your current location.</p>
          <button className="btn btn-danger" onClick={searchNearestHospital}>üìç Use My Location</button>
        </div>

        {nearestHospital ? (
          <div className="row justify-content-center">
            <div className="col-md-6 mb-4">
              <div className="card border-danger shadow-sm">
                <div className="card-body">
                  <h5 className="card-title text-danger">{nearestHospital.name}</h5>
                  <p className="card-text">
                    <strong>City:</strong> {nearestHospital.city} <br />
                    <strong>Pincode:</strong> {nearestHospital.pincode} <br />
                    <strong>Distance:</strong> {nearestHospital.distance.toFixed(2)} km
                  </p>
                  <button
                    className="btn btn-success"
                    onClick={() => notifyHospital(nearestHospital)}
                  >
                    üöë Notify Hospital
                  </button>
                </div>
              </div>
            </div>

            <div className="col-md-8">
              <MapContainer
                center={[nearestHospital.latitude, nearestHospital.longitude]}
                zoom={13}
                scrollWheelZoom={false}
                style={{ height: '300px', width: '100%' }}
              >
                <TileLayer
                  attribution='&copy; OpenStreetMap contributors'
                  url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                />
                <Marker position={[nearestHospital.latitude, nearestHospital.longitude]}>
                  <Popup>
                    {nearestHospital.name} <br /> {nearestHospital.city}
                  </Popup>
                </Marker>
                {userLocation && (
                  <Marker position={[userLocation.latitude, userLocation.longitude]}>
                    <Popup>
                      üìç Your Location
                    </Popup>
                  </Marker>
                )}
              </MapContainer>
            </div>
          </div>
        ) : (
          <div className="text-center text-muted">
            <p>No hospital found. Click "üìç Use My Location" to search again.</p>
          </div>
        )}
      </div>
    </div>
  );
}

export default Home;
