import React, { useContext, useState } from 'react';
import { NotificationContext } from '../context/NotificationContext';

function HospitalDashboard() {
  const { notifications, setNotifications } = useContext(NotificationContext);
  const hospitalName = localStorage.getItem('hospital');
  const hospitalId = notifications.find(notification => notification.hospitalName === hospitalName)?.hospitalId;

  const filteredNotifications = notifications.filter(note => note.hospitalId === hospitalId);

  const handleTakeAction = (index) => {
    const updatedNotifications = [...notifications];
    const noteIndex = notifications.findIndex(
      (note) => note.hospitalId === hospitalId && filteredNotifications[index] === note
    );
    updatedNotifications[noteIndex].status = 'In Progress';
    setNotifications(updatedNotifications);
  };

  return (
    <div className="container py-5">
      <div className="text-center mb-5">
        <h2 className="fw-bold text-danger">üè• Welcome, {hospitalName}</h2>
        <p className="text-muted">Monitor live accident alerts assigned to your hospital.</p>
      </div>

      <div className="card shadow-sm">
        <div className="card-body">
          <h4 className="card-title text-center mb-4 text-danger">üö® Real-time Accident Notifications</h4>

          {filteredNotifications.length === 0 ? (
            <div className="alert alert-info text-center">
              No notifications yet. Stay alert for incoming emergencies!
            </div>
          ) : (
            <ul className="list-group">
              {filteredNotifications.map((note, index) => (
                <li key={index} className="list-group-item">
                  <div className="d-flex justify-content-between align-items-center mb-2">
                    <div>
                      üìç <strong>{note.location}</strong> <br />
                      üìû <strong>{note.mobile}</strong> <br />
                      üåê Lat: {note.latitude}, Lng: {note.longitude}
                    </div>
                    <div className="text-end">
                      <span className={`badge ${note.status === 'Pending' ? 'bg-warning' : 'bg-success'}`}>
                        {note.status}
                      </span>
                      {note.status === 'Pending' && (
                        <button
                          className="btn btn-sm btn-outline-danger ms-2"
                          onClick={() => handleTakeAction(index)}
                        >
                          Take Action
                        </button>
                      )}
                    </div>
                  </div>
                  <div className="text-end text-muted">{note.time}</div>
                </li>
              ))}
            </ul>
          )}
        </div>
      </div>
    </div>
  );
}

export default HospitalDashboard;
