import React, { useContext } from 'react';
import { NotificationContext } from '../context/NotificationContext';

function HospitalDashboard() {
  const { notifications } = useContext(NotificationContext);
  const hospitalEmail = localStorage.getItem('hospitalEmail');
  const hospitalName = localStorage.getItem('hospitalName'); // assuming you save hospital name too

  // Filter notifications to show only those specific to the logged-in hospital
  const filteredNotifications = notifications.filter(note => note.hospitalEmail === hospitalEmail);

  return (
    <div className="container py-5">
      <div className="text-center mb-5">
        <h2 className="fw-bold text-danger">üè• Welcome, {hospitalName}</h2>
        <p className="text-muted">Monitor live accident alerts assigned to your hospital.</p>
      </div>

      <div className="card shadow-sm">
        <div className="card-body">
          <h4 className="card-title text-center mb-4 text-danger">üö® Real-time Accident Notifications</h4>

          {filteredNotifications.length === 0 ? (
            <div className="alert alert-info text-center">
              No notifications yet. Stay alert for incoming emergencies!
            </div>
          ) : (
            <div className="accordion" id="notificationsAccordion">
              {filteredNotifications.map((note, index) => (
                <div className="accordion-item" key={index}>
                  <h2 className="accordion-header" id={`heading${index}`}>
                    <button
                      className="accordion-button collapsed"
                      type="button"
                      data-bs-toggle="collapse"
                      data-bs-target={`#collapse${index}`}
                      aria-expanded="false"
                      aria-controls={`collapse${index}`}
                    >
                      üìç Accident at <strong className="mx-1">{note.location}</strong> | üïí {note.time}
                    </button>
                  </h2>
                  <div
                    id={`collapse${index}`}
                    className="accordion-collapse collapse"
                    aria-labelledby={`heading${index}`}
                    data-bs-parent="#notificationsAccordion"
                  >
                    <div className="accordion-body">
                      <p><strong>City:</strong> {note.location}</p>
                      <p><strong>Latitude:</strong> {note.latitude}</p>
                      <p><strong>Longitude:</strong> {note.longitude}</p>
                      <p><strong>Reported Time:</strong> {note.time}</p>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default HospitalDashboard;
