// src/App.js
import React, { useState } from "react";
import HospitalLogin from "./components/HospitalLogin";
import AccidentNotification from "./components/AccidentNotification";
import HospitalDashboard from "./components/HospitalDashboard";
import { NotificationProvider } from "./context/NotificationContext";

const App = () => {
  const [hospitalId, setHospitalId] = useState(null);

  const handleHospitalLogin = (id) => {
    setHospitalId(id);
  };

  return (
    <NotificationProvider>
      <div className="App">
        {hospitalId ? (
          <>
            <AccidentNotification hospitalId={hospitalId} />
            <HospitalDashboard hospitalId={hospitalId} />
          </>
        ) : (
          <HospitalLogin onHospitalLogin={handleHospitalLogin} />
        )}
      </div>
    </NotificationProvider>
  );
};

export default App;
