import React, { useState, useContext } from 'react';
import { hospitalsData } from '../data/hospitalsData';
import { NotificationContext } from '../context/NotificationContext';

function Home() {
  const [query, setQuery] = useState('');
  const [results, setResults] = useState([]);
  const { setNotifications } = useContext(NotificationContext);

  const searchHospitals = () => {
    const res = hospitalsData.filter(
      h => h.city.toLowerCase() === query.toLowerCase() || h.pincode === query
    );
    setResults(res);
  };

  const notifyHospital = (hospital) => {
    const location = prompt("Enter your accident location:");
    if (location) {
      setNotifications(prev => [
        ...prev,
        { hospitalId: hospital.id, hospitalName: hospital.name, location, time: new Date().toLocaleTimeString() }
      ]);
      alert(`ðŸš‘ Hospital "${hospital.name}" has been notified!`);
    }
  };

  return (
    <div className="container py-5">
      <div className="text-center mb-5">
        <h1 className="text-danger fw-bold">Accident Notifier</h1>
        <p className="text-muted">Find and notify nearby hospitals immediately after an accident</p>
      </div>

      <div className="row justify-content-center mb-4">
        <div className="col-md-6">
          <div className="input-group shadow-sm">
            <input
              type="text"
              className="form-control"
              placeholder="Enter City or Pincode"
              value={query}
              onChange={(e) => setQuery(e.target.value)}
            />
            <button className="btn btn-danger" onClick={searchHospitals}>Search</button>
          </div>
        </div>
      </div>

      <div className="row">
        {results.length > 0 ? results.map(hospital => (
          <div key={hospital.id} className="col-md-4 mb-4">
            <div className="card border-danger shadow-sm h-100">
              <div className="card-body d-flex flex-column justify-content-between">
                <div>
                  <h5 className="card-title text-danger">{hospital.name}</h5>
                  <p className="card-text">
                    <strong>City:</strong> {hospital.city} <br />
                    <strong>Pincode:</strong> {hospital.pincode}
                  </p>
                </div>
                <button className="btn btn-success mt-3" onClick={() => notifyHospital(hospital)}>
                  ðŸš‘ Notify Hospital
                </button>
              </div>
            </div>
          </div>
        )) : (
          <div className="text-center text-muted">
            <p>No hospitals found. Please search by a valid city or pincode.</p>
          </div>
        )}
      </div>
    </div>
  );
}

export default Home;
