import React, { useState, useContext } from 'react';
import { hospitalsData } from '../data/hospitalsData';
import { NotificationContext } from '../context/NotificationContext';

function Home() {
  const [query, setQuery] = useState('');
  const [results, setResults] = useState([]);
  const { setNotifications } = useContext(NotificationContext);

  const searchHospitals = () => {
    const res = hospitalsData.filter(
      h => h.city.toLowerCase() === query.toLowerCase() || h.pincode === query
    );
    setResults(res);
  };

  const notifyHospital = (hospital) => {
    const location = prompt("Enter your accident location:");
    if (location) {
      setNotifications(prev => [
        ...prev,
        { hospitalId: hospital.id, hospitalName: hospital.name, location, time: new Date().toLocaleTimeString() }
      ]);
      alert(`üöë Hospital "${hospital.name}" has been notified!`);
    }
  };

  return (
    <div className="container-fluid p-0">
      {/* Hero Section */}
      <div className="bg-dark text-white text-center py-5" style={{ backgroundImage: 'url(https://images.unsplash.com/photo-1602851844960-8a5de0c2f14a?ixlib=rb-4.0.3&auto=format&fit=crop&w=1350&q=80)', backgroundSize: 'cover', backgroundPosition: 'center', backgroundBlendMode: 'overlay' }}>
        <div className="container">
          <h1 className="display-4 fw-bold">Accident Detection & Emergency Notifier</h1>
          <p className="lead">Every second matters. Find nearby hospitals and notify them instantly during accidents.</p>
        </div>
      </div>

      {/* Accident Info Section */}
      <div className="container my-5">
        <div className="row align-items-center">
          <div className="col-md-6">
            <img src="data:image/jpeg;base64," alt="Accident awareness" className="img-fluid rounded shadow" />
          </div>
          <div className="col-md-6">
            <h2 className="text-danger">Why Immediate Action Matters?</h2>
            <p>
              Road accidents claim millions of lives every year. Timely medical intervention can reduce fatalities by up to 50%.
              Our platform helps you connect with the nearest hospitals within seconds ‚Äî because your life matters.
            </p>
            <ul className="list-group list-group-flush">
              <li className="list-group-item">üöë 1.3 million deaths occur yearly due to road accidents (WHO)</li>
              <li className="list-group-item">üïê Immediate care within the "Golden Hour" saves lives</li>
              <li className="list-group-item">üìç Real-time hospital notification reduces waiting time</li>
            </ul>
          </div>
        </div>
      </div>

      {/* Search Section */}
      <div className="container py-5">
        <div className="text-center mb-5">
          <h2 className="text-danger fw-bold">Find Nearby Hospitals</h2>
          <p className="text-muted">Enter your location to find the closest hospitals ready to assist you.</p>
        </div>

        <div className="row justify-content-center mb-4">
          <div className="col-md-6">
            <div className="input-group shadow-sm">
              <input
                type="text"
                className="form-control"
                placeholder="Enter City or Pincode"
                value={query}
                onChange={(e) => setQuery(e.target.value)}
              />
              <button className="btn btn-danger" onClick={searchHospitals}>Search</button>
            </div>
          </div>
        </div>

        {/* Hospital Results */}
        <div className="row">
          {results.length > 0 ? results.map(hospital => (
            <div key={hospital.id} className="col-md-4 mb-4">
              <div className="card border-danger shadow-sm h-100">
                <div className="card-body d-flex flex-column justify-content-between">
                  <div>
                    <h5 className="card-title text-danger">{hospital.name}</h5>
                    <p className="card-text">
                      <strong>City:</strong> {hospital.city} <br />
                      <strong>Pincode:</strong> {hospital.pincode}
                    </p>
                  </div>
                  <button className="btn btn-success mt-3" onClick={() => notifyHospital(hospital)}>
                    üöë Notify Hospital
                  </button>
                </div>
              </div>
            </div>
          )) : (
            <div className="text-center text-muted">
              <p>No hospitals found. Please search by a valid city or pincode.</p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default Home;
