// src/components/AccidentNotification.js
import React, { useState, useContext } from "react";
import { NotificationContext } from "../context/NotificationContext";

const AccidentNotification = ({ hospitalId }) => {
  const [message, setMessage] = useState("");
  const { addNotification } = useContext(NotificationContext);

  const handleNotify = () => {
    if (message.trim()) {
      addNotification(hospitalId, message);
      setMessage(""); // Reset the message field
    }
  };

  return (
    <div>
      <h2>Notify Hospital</h2>
      <textarea
        value={message}
        onChange={(e) => setMessage(e.target.value)}
        placeholder="Enter accident location and details"
      />
      <button onClick={handleNotify}>Notify</button>
    </div>
  );
};

export default AccidentNotification;
