// src/context/NotificationContext.js
import React, { createContext, useState } from "react";

export const NotificationContext = createContext();

export const NotificationProvider = ({ children }) => {
  const [notifications, setNotifications] = useState([]);

  // Add a notification linked to a hospital
  const addNotification = (hospitalId, message) => {
    setNotifications([
      ...notifications,
      { hospitalId, message, timestamp: new Date().toLocaleString() },
    ]);
  };

  // Get notifications for a specific hospital
  const getNotifications = (hospitalId) => {
    return notifications.filter((notification) => notification.hospitalId === hospitalId);
  };

  return (
    <NotificationContext.Provider value={{ addNotification, getNotifications }}>
      {children}
    </NotificationContext.Provider>
  );
};
