import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

function HospitalLogin() {
  const navigate = useNavigate();
  const [hospitalName, setHospitalName] = useState('');
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const handleLogin = () => {
    if (hospitalName && username && password) {
      // You could also verify credentials here with an API call
      localStorage.setItem('hospital', hospitalName);
      localStorage.setItem('username', username);
      navigate('/hospital-dashboard');
    } else {
      alert('Please fill in all fields');
    }
  };

  return (
    <div className="d-flex align-items-center justify-content-center vh-100 bg-light">
      <div className="card p-4 shadow" style={{ width: '100%', maxWidth: '400px' }}>
        <h2 className="text-center text-danger mb-4">Hospital Login</h2>
        
        <input
          type="text"
          className="form-control mb-3"
          placeholder="Hospital Name"
          value={hospitalName}
          onChange={(e) => setHospitalName(e.target.value)}
        />

        <input
          type="text"
          className="form-control mb-3"
          placeholder="Username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
        />

        <input
          type="password"
          className="form-control mb-3"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />

        <button className="btn btn-danger w-100" onClick={handleLogin}>
          Login
        </button>
      </div>
    </div>
  );
}

export default HospitalLogin;
