// src/App.js
import React, { useState } from "react";
import Home from "./components/Home";
import HospitalLogin from "./components/HospitalLogin";
import HospitalDashboard from "./components/HospitalDashboard";
import SearchNearby from "./components/SearchNearby";
import { NotificationProvider } from "./context/NotificationContext";

const App = () => {
  const [page, setPage] = useState("home");
  const [hospitalId, setHospitalId] = useState(null);

  const handleHospitalLogin = (id) => {
    setHospitalId(id);
    setPage("dashboard");
  };

  const handleSelectOption = (option) => {
    setPage(option);
  };

  return (
    <NotificationProvider>
      <div className="App">
        {page === "home" && <Home onSelectOption={handleSelectOption} />}
        {page === "hospitalLogin" && <HospitalLogin onHospitalLogin={handleHospitalLogin} />}
        {page === "searchNearby" && <SearchNearby />}
        {page === "dashboard" && hospitalId && <HospitalDashboard hospitalId={hospitalId} />}
      </div>
    </NotificationProvider>
  );
};

export default App;
