// src/components/HospitalLogin.js
import React, { useState } from "react";
import { hospitalsData } from "../data/hospitalsData";

const HospitalLogin = ({ onHospitalLogin }) => {
  const [hospitalName, setHospitalName] = useState("");
  const [password, setPassword] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();

    const hospital = hospitalsData.find(
      (h) => h.name === hospitalName && h.password === password
    );

    if (hospital) {
      onHospitalLogin(hospital.id);
    } else {
      alert("Invalid hospital name or password. Please try again!");
    }
  };

  return (
    <div>
      <h2>Hospital Login</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Hospital Name:</label>
          <select value={hospitalName} onChange={(e) => setHospitalName(e.target.value)} required>
            <option value="">Select a Hospital</option>
            {hospitalsData.map((hospital) => (
              <option key={hospital.id} value={hospital.name}>
                {hospital.name}
              </option>
            ))}
          </select>
        </div>

        <div>
          <label>Password:</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>

        <button type="submit">Login</button>
      </form>
    </div>
  );
};

export default HospitalLogin;
